INSERT INTO public.resources (id, isbn, title, authors, genres, description, cover_image, current_possessor_id, owner_id, created_at, updated_at) VALUES (42, '9780596157135', 'Learning PHP, MySQL & JavaScript', 'Robin Nixon', 'Programming', 'Learning PHP, MySQL & JavaScript will teach you how to create responsive, data-driven websites with the three central technologies of PHP, MySQL and JavaScript - whether or not you know how to program. This simple, streamlined guide explains how the powerful combination of PHP and MySQL provides a painless way to build modern websites with dynamic data and user interaction. You''ll also learn how to add JavaScript to create rich Internet websites and applications, and how to use Ajax to handle background communication with a web server. This book explains each technology separately, shows you how to combine them, and introduces valuable concepts in modern web programming, including objects, XHTML, cookies, regular expressions and session management.', 'https://covers.openlibrary.org/b/isbn/9780596157135-L.jpg', 2, 7, '2022-05-13 10:28:56.776379', '2022-05-13 10:28:56.776379');
